where matchId = #{matchId}
and riotIdGameName = #{riotIdGameName}
and riotIdTagline =
#{riotIdTagline};

		UPDATE RiotGameInfoT
		SET tier = #{tier}
		WHERE matchId =
		#{matchId} and riotIdGameName = #{riotIdGameName};

		select * from
		RiotGameInfoT
		where matchId = #{matchId}
		and riotIdGameName =
		#{riotIdGameName}
		and riotIdTagline = #{riotIdTagline};

mapper.update:
UPDATE RiotGameInfoT
SET airesult = #{key}
WHERE
matchId = #{matchId} and participantId = #{participantId};

mapper.select:
select * from
RiotGameInfoT
where matchId = #{matchId}
and riotIdGameName = #{riotIdGameName}
and riotIdTagline =
#{riotIdTagline};

select * from
aiTimelineT
where matchId = #{matchId}
order by timestamp;

select * from
RiotGameInfoT
where matchId = #{matchId}
order by participantId asc;

select teamPosition from RiotGameInfoT where
riotIdGameName = #{gameName}
limit 0,19;

select matchId
from RiotGameInfoT
where riotIdGameName =#{gameName}
order by matchId desc
limit #{matchCnt}, 3;

select matchId
from RiotGameInfoT
where riotIdGameName = #{gameName} and queueId = #{queueId}
order by matchId desc
limit #{matchCnt}, 3;

select queueId,queueId_kor
from RiotGameInfoT
where riotIdGameName = #{riotIdGameName}
group by queueId,queueId_kor;

select matchId
from RiotGameInfoT
where riotIdGameName = #{gameName}
group by matchId;
