mapper.select:
select champion_name_kr,champion_name 
from champList
where champion_name_kr like concat('%',#{searchChamp},'%') or champion_name like concat('%',#{searchChamp},'%') or champion_name_cons like concat('%',#{searchChamp},'%');

select champion_name_kr,champion_name
from champList
where line like concat('%',#{line},'%');
