mapper.select:
select *
from  memberT 
where userId = #{userId} and userPw = #{userPw}

select count(*) 
from memberT
 where userId = #{userId}

mapper.update:
update memberT
set userPoint = 
(select userPoint from memberT where userId = #{userId})+#{userPoint}
where userId = #{userId}

mapper.select:

insert into duoT(userId,myPosition,tier,duoPosition,gameType,memo)
 values(#{userId},#{myPosition},#{tier},#{duoPosition},#{gameType},#{memo});

select * from duoT order by dcnt desc limit 1;
